mapper.select:
select

from mmall_user
where user_name = #{userName}
and password = #{password}

SELECT
count(1)
from mmall_user
where username = #{userName}
and question = #{question}
and answer = #{answer}

mapper.update:
update mmall_user
set password = #{newPassword}, update_time = now()
where username = #{userName}

mapper.select:
select
count(1)
from mmall_user
where password = #{password}
and id = #{userId}

select count(1) from mmall_user
where email = #{email}
and id != #{userId}
